func TestXzver(t *testing.T) {
	var cmdbuf bytes.Buffer
	var fake faker
	fake.Writer = &cmdbuf

	conn := &Conn{conn: fake, w: fake, r: bufio.NewReader(strings.NewReader(xzverServer))}

	_, err := conn.Group("alt.battlestar-galactica")
	if err != nil {
		t.Fatal("Group shouldn't error: " + err.Error())
	}

	if articles, err := conn.Overview(160000, 160100); err != nil {
		t.Fatal("Overview shouldn't error: " + err.Error())
	} else if len(articles) != 101 {
		t.Fatalf("Expected 101 articles, got: %+v", articles)
	}
	
	actualcmds := cmdbuf.String()
	if xzverClient != actualcmds {
		t.Fatalf("Got: %q\nExpected: %q", actualcmds, xzverClient)
	}
}

